CREATE TABLE IF NOT EXISTS `ims_wjyk_zqds_address` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `uid` varchar(155) NOT NULL, `consignee` varchar(155) NOT NULL COMMENT '收货人姓名', `consignee_phone` varchar(155) NOT NULL COMMENT '收货人电话', `province` varchar(100) NOT NULL COMMENT '省', `city` varchar(100) NOT NULL COMMENT '市', `county` varchar(100) NOT NULL COMMENT '区', `location` varchar(255) NOT NULL COMMENT '省/市/区', `detail` varchar(255) NOT NULL COMMENT '详细地址', `is_default` smallint(1) NOT NULL DEFAULT '2' COMMENT '1-默认，2-非默认', `createtime` int(10) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='收货地址表';
