CREATE TABLE IF NOT EXISTS `ims_wjyk_zqds_task_set` ( `id` int(11) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL COMMENT '公众号id', `is_open` smallint(1) NOT NULL DEFAULT '2' COMMENT '1-开启，2-隐藏', `advert_icon` text COMMENT '观看广告图标', `advert_name` varchar(255) DEFAULT NULL COMMENT '观看广告文字', `advert_count` int(3) DEFAULT '0' COMMENT '每日观看广告数量', `advert_integral` int(3) DEFAULT '0' COMMENT '每日观看广告数量可获得金豆', `manor_icon` text COMMENT '喂牛图标', `manor_name` varchar(255) DEFAULT NULL COMMENT '喂牛文字', `manor_count` int(3) DEFAULT '0' COMMENT '每日喂牛次数', `manor_integral` int(3) DEFAULT '0' COMMENT '每日喂牛次数可获得金豆', `invite_icon` text COMMENT '邀请好友图标', `invite_name` varchar(255) DEFAULT NULL COMMENT '邀请好友文字', `invite_count` int(3) DEFAULT '0' COMMENT '邀请好友次数', `invite_integral` int(3) DEFAULT '0' COMMENT '邀请好友可获得金豆', `huafei_icon` text COMMENT '充话费图标', `huafei_name` varchar(255) DEFAULT NULL COMMENT '充话费文字', `huafei_count` int(3) DEFAULT '0' COMMENT '充话费次数', `huafei_integral` int(3) DEFAULT '0' COMMENT '充话费可获得金豆', `news_icon` text COMMENT '观看新闻图标', `news_name` varchar(255) DEFAULT NULL COMMENT '观看新闻文字', `news_count` int(3) DEFAULT '0' COMMENT '观看新闻次数', `news_integral` int(3) DEFAULT '0' COMMENT '观看新闻可获得金豆', `createtime` int(10) NOT NULL, `takeout_desc` varchar(255) NOT NULL COMMENT '外卖描述文字', `invite_desc` varchar(255) NOT NULL COMMENT '邀请好友描述文字', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='任务中心配置表';
CREATE TABLE IF NOT EXISTS `ims_wjyk_zqds_upload` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `type` smallint(1) NOT NULL DEFAULT '1' COMMENT '1-本地，2-七牛云 3-阿里云 4-腾讯云', `accesskey` varchar(255) NOT NULL COMMENT '七牛云accesskey', `secretkey` varchar(255) NOT NULL COMMENT '七牛云secretkey', `bucket` varchar(255) NOT NULL COMMENT '七牛云bucket', `url` varchar(255) NOT NULL COMMENT '七牛云url', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='上传配置表';
CREATE TABLE IF NOT EXISTS `ims_wjyk_zqds_user` ( `id` int(11) NOT NULL AUTO_INCREMENT, `pid` varchar(155) NOT NULL, `uniacid` int(11) NOT NULL, `avatar` varchar(255) NOT NULL, `nickname` varchar(255) NOT NULL, `openid` varchar(155) NOT NULL, `balance` decimal(20,2) DEFAULT '0.00' COMMENT '余额', `qrcode` text COMMENT '分销二维码', `pay_qrcode` text COMMENT '收款码', `zfb_account` text COMMENT '支付宝账号', `sign_time` varchar(255) NOT NULL COMMENT '上次签到时间', `sign_day` int(10) DEFAULT '0' COMMENT '已签到天数', `is_member` smallint(1) DEFAULT '0' COMMENT '0非会员', `maturity_time` int(10) DEFAULT '0' COMMENT '到期时间', `relation_id` varchar(255) NOT NULL DEFAULT '' COMMENT '淘宝渠道id', `createtime` int(10) NOT NULL, `is_block` smallint(1) DEFAULT '0' COMMENT '0未拉黑 1-已拉黑', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户表';
CREATE TABLE IF NOT EXISTS `ims_wjyk_zqds_wxapp` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) NOT NULL, `sort` int(11) DEFAULT '0', `icon` text COMMENT '图标', `name` varchar(100) NOT NULL COMMENT '名称', `appid` varchar(255) NOT NULL COMMENT 'appid', `path` varchar(255) NOT NULL COMMENT '路径', `createtime` int(10) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='小程序表';
