CREATE TABLE IF NOT EXISTS `ims_xc_beauty_address` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `openid` varchar(50) DEFAULT NULL COMMENT '用户id', `name` varchar(50) DEFAULT NULL COMMENT '姓名', `mobile` varchar(50) DEFAULT NULL COMMENT '手机号', `address` longtext COMMENT '地址', `map` longtext COMMENT '地址', `content` longtext COMMENT '地址', `status` int(11) DEFAULT '-1' COMMENT '状态', `createtime` datetime DEFAULT NULL COMMENT '添加时间', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`openid`,`status`,`createtime`) ) ENGINE=InnoDB AUTO_INCREMENT=160 DEFAULT CHARSET=utf8 COMMENT='地址';
CREATE TABLE IF NOT EXISTS `ims_xc_beauty_apply` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `openid` varchar(50) DEFAULT NULL COMMENT '用户id', `name` varchar(50) DEFAULT NULL COMMENT '姓名', `mobile` varchar(50) DEFAULT NULL COMMENT '电话', `address` varchar(255) DEFAULT NULL COMMENT '地址', `map` longtext COMMENT '地址', `content` varchar(255) DEFAULT NULL COMMENT '详细地址', `status` int(11) DEFAULT '-1' COMMENT '状态（-1审核中1审核通过2失败3失败已阅）', `createtime` datetime DEFAULT NULL COMMENT '申请时间', `applytime` datetime DEFAULT NULL COMMENT '处理时间', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`status`,`createtime`,`applytime`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='审核';
CREATE TABLE IF NOT EXISTS `ims_xc_beauty_area` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `name` varchar(50) DEFAULT NULL COMMENT '名称', `title` varchar(255) DEFAULT NULL COMMENT '副标题', `bimgt` varchar(255) DEFAULT NULL COMMENT '上背景图', `bimgb` varchar(255) DEFAULT NULL COMMENT '下背景图', `down_status` int(11) DEFAULT '-1' COMMENT '满立减', `down_title` varchar(50) DEFAULT NULL COMMENT '小标题', `order_status` int(11) DEFAULT '-1' COMMENT '下单送券', `order_title` varchar(50) DEFAULT NULL COMMENT '小标题', `pay_type` int(11) DEFAULT '-1' COMMENT '充值优惠', `pay_title` varchar(255) DEFAULT NULL COMMENT '小标题', `content` longtext, `status` int(11) DEFAULT '1' COMMENT '状态', `createtime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间', `backcolor` varchar(50) DEFAULT NULL COMMENT '背景色', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`status`,`createtime`) ) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='新人专区';
CREATE TABLE IF NOT EXISTS `ims_xc_beauty_area_item` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `cid` int(11) DEFAULT NULL, `type` int(11) DEFAULT '1' COMMENT '类型（1满立减2送券3充值优惠）', `simg` varchar(255) DEFAULT NULL COMMENT '图标', `amount` varchar(50) DEFAULT NULL COMMENT '满足金额', `price` varchar(50) DEFAULT NULL COMMENT '减去金额', `start_time` datetime DEFAULT NULL COMMENT '开始时间', `end_time` datetime DEFAULT NULL COMMENT '结束时间', `order_num` int(11) DEFAULT NULL COMMENT '完成顺序', `order_type` int(11) DEFAULT '1' COMMENT '完成奖励（1优惠券2兑换券）', `coupon` int(11) DEFAULT NULL COMMENT '优惠券', `service` int(11) DEFAULT NULL COMMENT '商品', `service_end` datetime DEFAULT NULL, `status` int(11) DEFAULT '1' COMMENT '状态', `sort` int(11) DEFAULT '0' COMMENT '排序', `createtime` datetime DEFAULT NULL COMMENT '添加时间', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`cid`,`type`,`status`,`sort`,`createtime`) ) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='礼包内容';
CREATE TABLE IF NOT EXISTS `ims_xc_beauty_area_log` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `openid` varchar(50) DEFAULT NULL COMMENT '用户id', `cid` int(11) DEFAULT NULL, `type` int(11) DEFAULT NULL COMMENT '类型（1满立减2送券3充值优惠）', `simg` varchar(255) DEFAULT NULL COMMENT '图标', `amount` decimal(10,2) DEFAULT NULL COMMENT '满足金额', `price` decimal(10,2) DEFAULT NULL COMMENT '减去金额', `start_time` datetime DEFAULT NULL COMMENT '开始时间', `end_time` datetime DEFAULT NULL COMMENT '结束时间', `order_num` int(11) DEFAULT NULL COMMENT '完成顺序', `order_type` int(11) DEFAULT '1' COMMENT '完成奖励（1优惠券2兑换券）', `coupon` int(11) DEFAULT NULL COMMENT '优惠券', `service` int(11) DEFAULT NULL COMMENT '商品', `service_end` datetime DEFAULT NULL, `prize` int(11) DEFAULT '-1' COMMENT '领取状态（-1未领取1领取）', `status` int(11) DEFAULT '-1' COMMENT '状态（-1未完成1已完成）', `createtime` datetime DEFAULT NULL COMMENT '添加时间', PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`,`cid`,`type`,`status`,`createtime`) ) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COMMENT='礼包记录';
